def home(request):
    query = request.GET.get('s','')
    if query:
        queryset = (Q(reporter__icontains=query))
        results_list = Articles.objects.filter(queryset).distinct()
        page = request.GET.get('page', 1)
        paginator = Paginator(results_list, 10)
        try:
            results = paginator.page(page)
        except PageNotAnInteger:
            results = paginator.page(1)
        except EmptyPage:
            results = paginator.page(paginator.num_pages)
    else:
       results = []
    context = {
        'article_1': Articles.objects.get(pk=6),
        'article_2': Articles.objects.get(pk=7),
        'article_3': Articles.objects.get(pk=8),
        'article_4': Articles.objects.get(pk=9),
        'report_1': Articles.objects.get(pk=10),
        'report_2': Articles.objects.get(pk=11),
        'news_1': News.objects.get(pk=1),
        'news_2': News.objects.get(pk=2),
        'news_3': News.objects.get(pk=3),
        'results': results,
        'query':query
    }
    return render(request, 'home.html', context)

