def insights(request):
    global bba_subscription
    global bba_perspective
    global bba_report
    global bba_article
    global bba_case_study
    
    # if this is a POST request we need to process the form data
    if request.method == 'POST':
        print("Recieved form data", flush=True)
        # process the data in form.cleaned_data as required
        if request.POST.get("bba_subscription", False):
            bba_subscription = "Subscription"
        else:
            bba_subscription = None
        if request.POST.get("bba_perspective", False):
            bba_perspective = "Perspective"
        else:
            bba_perspective = None
        if request.POST.get("bba_report", False):
            bba_report = "Report"
        else:
            bba_report = None
        if request.POST.get("bba_article", False):
            bba_article = "Article"
        else:
            bba_article = None
        if request.POST.get("bba_case_study", False):
            bba_case_study = "CASE STUDIES"
        else:
            bba_case_study = None

    insights_list = Articles.objects.filter(Q(classif=bba_subscription)|Q(classif=bba_perspective)|Q(classif=bba_report)|Q(classif=bba_article)|Q(classif=bba_case_study))
    
    page = request.GET.get('page', 1)
    paginator = Paginator(insights_list, 10)
    try:
        insights = paginator.page(page)
    except PageNotAnInteger:
        insights = paginator.page(1)
    except EmptyPage:
        insights = paginator.page(paginator.num_pages)
    
    context = {
        'insights': insights,
        'bba_subscription': bba_subscription,
        'bba_perspective': bba_perspective,
        'bba_report': bba_report,
        'bba_article': bba_article,
        'bba_case_study': bba_case_study
    }

    return render(request, 'insights.html', context)

